pragma solidity ^0.5.0;


contract Toy {
    // struct of a pet's info, picture = url of picture
    struct Pet{
        uint id;
        string name;
        string picture; 
        uint age;
        string breed;
        string location;
        address payable adopter;
        bool isForSale;
        uint price;
        bool isPurchased;
        bool isAdopted;
    }


    // mapping from petsNum to pets' info
    mapping(uint => Pet) public pets;

    mapping(uint => uint256) public adoptedTime;

    // petsNum are positive Integer, representing number of pets
    uint public petsNum;
    // myAddr is our wallet address
    string myAddr = "0xf35c952cA340425bc614CD209D2887C3F6cd5fBF";

    constructor() public {
        petsNum = 0;
    }

    // Adopting a pet
    function adopt(uint petId) public returns (uint) {
    }

    // Buying a pet
    function buy(uint petId) payable public{
    }

    function vote(uint petId) public returns (uint){
    }

    // Retrieving the voted Pet for the current user
    function getVotedPets() public view returns (uint[100] memory) {
    }

    // Retrieving the voted Pet for the current user
    function getliked(uint petId) public view returns (uint) {
    }

    // Adding a pet
    function add(string memory name, string memory picture, uint age, string memory breed,
        string memory location, bool isForSale, uint price) public returns (uint) {

        if (isForSale){
            pets[petsNum] = Pet(petsNum, name, picture, age, breed, location, msg.sender, isForSale, price, false, false);
        }else{
            pets[petsNum] = Pet(petsNum, name, picture, age, breed, location, msg.sender, isForSale, 0, false, false);
        }

        petsNum++;
        return petsNum;
    }

    // Retrieving the numbers of pets
    function getPetsNum() public view returns (uint) {
        return petsNum;
    }

    // Retrieving the pet's details
    function getPetDetails(uint petId) public view returns (uint, string memory, string memory, uint, string memory,
        string memory, address payable, bool, uint, bool, bool) {
        require(petId >= 0 && petId < petsNum);
        Pet memory p = pets[petId];
        return (p.id, p.name, p.picture, p.age, p.breed, p.location, p.adopter, p.isForSale, p.price, p.isPurchased, p.isAdopted);
    }

    // Retrieving whether the pet is for sale or for adoption
    function isForSale(uint petId) public view returns (uint, bool) {
        require(petId >= 0 && petId < petsNum);
        Pet memory p = pets[petId];
        return (p.id, p.isForSale);
    }

    // Retrieving myAddr Address
    function getMyAddr() public view returns (string memory){
    }

}
